core/account/_private/layout:
{{!
  A wrapper for all account pages.

  @block account-heading
         The page's title.

  @block account-content
         The content for the main part of the layout.

  @param class string
         A name for the current page, added as a class to the account wrapper.

  @param nav string
         If "hidden", no nav will be shown.

  @param nav_page string
         If matches a string in navigation.html, that link will be made active.

  @param header string
         If "hidden", no header will be shown.
         If "top", header will be shown above the nav.
         Otherwise, header will be shown above the content.
}}
<section class="account-wrapper account-page-{{class}}{{#if nav '===' 'hidden'}} account-nav-hidden{{/if}}">
  {{#if header '===' 'top'}}{{> core/account/_private/layout-heading}}{{/if}}

  {{#if nav '!==' 'hidden'}}
    {{> core/account/_private/navigation nav_page=nav_page}}
  {{/if}}

  <div class="account-body">
    {{#unless header}}{{> core/account/_private/layout-heading}}{{/unless}}

    <div class="account-content">
      {{#block 'account-content'}}{{/block}}
    </div>
  </div>
</section>

core/account/recent-items:
{{#partial 'account-heading'}}
  {{lang 'core.account.recent_items.heading'}}
{{/partial}}

{{#partial 'account-content'}}
  {{#if customer.recently_viewed_products.items}}
    <form class="form" action="{{customer.recently_viewed_products.compare_url}}" method="post">
      <div class="account-recently-viewed account-product-wrapper {{products_per_row_class}}">
        {{!-- TODO: Limit to 3 products? --}}
        {{#each customer.recently_viewed_products.items}}
          {{> core/products/product-item default_image=../theme_settings.default_image crop=../crop}}
        {{/each}}
      </div>
    </form>
  {{else}}
    <p>{{lang 'core.account.recent_items.empty'}}</p>
  {{/if}}
{{/partial}}

{{> core/account/_private/layout nav_page='recent_items' class='recent-items'}}

core/products/product-item:
{{!
  A single product, as shown in a listing.

  @param this object
         A product object.

  @param show_quickshop bool
         If true, include a link that can be used to open a quickshop modal.

  @param show_compare bool
         If true, include a checkbox to select products to compare.

  @param show_remove bool
         If true, include remove from cart form. Requires `remove_url` to be
         available.

  @param show_rating bool
         If true, include the product's rating if there are enough votes.
}}
<article class="product-grid-item">
  {{#if out_of_stock_message.length}}
    <span class="product-grid-item-sale-badge">{{out_of_stock_message}}</span>
  {{else}}
    {{#if price.saved}}
      <span class="product-grid-item-sale-badge">{{lang 'product.on_sale'}}</span>
    {{/if}}
  {{/if}}
  <figure class="product-grid-item-thumbnail {{#if crop}}cropped-thumbnail aspect-{{crop}}{{/if}}">
    <a class="{{#if crop}}cropped-thumbnail-block{{/if}}" href="{{url}}" title="{{name}}"
      style="background-image:url({{#if image}}{{getImage image 'thumbnail'}}{{else}}{{cdn '/assets/icons/icon-no-image.svg'}}{{/if}});">

      {{#if image}}
        <img src="{{getImage image 'thumbnail'}}" alt="{{image.alt}}">
      {{else}}
        <img src="{{cdn '/assets/icons/icon-no-image.svg'}}" alt="{{lang 'product.no_image'}}">
      {{/if}}
    </a>

    {{#if rating}}
      <div class="product-grid-item-rating-wrap">
        <div class="product-grid-item-rating">
          {{> components/products/ratings rating=rating size="medium"}}
        </div>
      </div>
    {{/if}}

    {{#if show_compare}}
      <div class="product-item-compare">
        <div class="form-field form-field-inline form-field-checkbox">
          <div class="form-field-control">
            <label class="form-label" for="compare-{{id}}">
              <input type="checkbox" class="form-input form-checkbox" name="products[]" value="{{id}}" id="compare-{{id}}">
              <span class="form-label-text">{{lang "products.compare"}}</span>
            </label>
          </div>
        </div>
      </div>
    {{/if}}

    <span class="quick-shop-trigger" data-quick-shop-trigger="{{id}}"><i class="fa fa-shopping-cart"></i></span>
  </figure>

  <div class="product-grid-item-details">
    <h3 class="product-item-title">
      <a href="{{url}}" title="{{name}}">
        {{name}}
      </a>
    </h3>

    <div class="product-grid-item-price">
      {{> components/products/price price=price}}
    </div>
  </div>

</article>
