core/account/_private/layout:
{{!
  A wrapper for all account pages.

  @block account-heading
         The page's title.

  @block account-content
         The content for the main part of the layout.

  @param class string
         A name for the current page, added as a class to the account wrapper.

  @param nav string
         If "hidden", no nav will be shown.

  @param nav_page string
         If matches a string in navigation.html, that link will be made active.

  @param header string
         If "hidden", no header will be shown.
         If "top", header will be shown above the nav.
         Otherwise, header will be shown above the content.
}}
<section class="account-wrapper account-page-{{class}}{{#if nav '===' 'hidden'}} account-nav-hidden{{/if}}">
  {{#if header '===' 'top'}}{{> core/account/_private/layout-heading}}{{/if}}

  {{#if nav '!==' 'hidden'}}
    {{> core/account/_private/navigation nav_page=nav_page}}
  {{/if}}

  <div class="account-body">
    {{#unless header}}{{> core/account/_private/layout-heading}}{{/unless}}

    <div class="account-content">
      {{#block 'account-content'}}{{/block}}
    </div>
  </div>
</section>

core/account/_private/receipt-items:
{{#if order.items}}
  <div class="account-items account-receipt-items">
    <h2 class="account-sub-heading">{{lang 'core.account.receipt.items.title'}}</h2>

    {{#each order.items}}
      <article class="account-item">
        <div class="account-item-thumbnail">
          {{#if type '===' 'giftcertificate'}}
            <img src="{{cdn ../../theme_settings.default_image_gift_certificate}}">
          {{else}}
            <img src="{{getImage image 'account-product-thumbnail' (cdn ../../theme_settings.default_image)}}" alt="{{image.alt}}">
          {{/if}}
        </div>

        <div class="account-item-body">
          <h1 class="account-item-title">
            {{lang 'core.account.receipt.items.product_title' name=name quantity=quantity}}
          </h1>

          <div class="account-item-description">
            {{#if brand}}
              <div class="account-receipt-item-brand">{{brand.name}}</div>
            {{/if}}

            {{#if download_url}}
              {{#unless refunded}}
                <a class="button account-button-primary account-button-small account-receipt-download" href="{{download_url}}">{{lang 'core.account.receipt.items.download'}}</a>
              {{/unless}}
            {{/if}}
          </div>

          <div class="account-item-details">
            {{#if options}}
              <div class="account-item-detail">
                <h2 class="account-item-detail-title">{{lang 'core.account.receipt.items.options'}}</h2>
                <div class="account-item-detail-value account-receipt-item-options">
                  {{#each options}}
                    <div class="account-receipt-item-option">
                      <span class="account-receipt-option-name">{{name}}:</span>
                      <span class="account-receipt-option-value">{{value}}</span>
                    </div>
                  {{/each}}
                </div>
              </div>
            {{/if}}

            {{#if customizations}}
              <div class="account-item-detail">
                <h2 class="account-item-detail-title">{{lang 'core.account.receipt.items.customizations'}}</h2>
                <div class="account-item-detail-value account-receipt-item-options">
                  {{#each customizations}}
                    <div class="account-receipt-item-option">
                      <span class="account-receipt-option-name">{{name}}:</span>
                      <span class="account-receipt-option-value">{{value}}</span>
                    </div>
                  {{/each}}
                </div>
              </div>
            {{/if}}
          </div>

          <div class="account-item-details">
            {{#if gift_wrapping_name}}
              <div class="account-item-detail">
                <h2 class="account-item-detail-title">{{lang 'core.account.receipt.items.gift_wrapping'}}</h2>
                <span class="account-item-detail-value">{{gift_wrapping_name}}</span>
              </div>
            {{/if}}

            {{#if refunded}}
              <div class="account-item-detail">
                <span class="account-item-detail-title">{{lang 'core.account.receipt.items.refunded'}}</span>
                <span class="account-item-detail-value">{{refunded_qty}}</span>
              </div>
            {{else}}
              {{#if refunded_qty}}
                <div class="account-item-detail">
                  <span class="account-item-detail-title">{{lang 'core.account.receipt.items.refunded'}}</span>
                  <span class="account-item-detail-value">{{refunded_qty}}</span>
                </div>
              {{/if}}
            {{/if}}

            {{#unless show_reorder}}
              <div class="account-item-detail">
                <span class="account-item-detail-title">{{lang 'core.account.receipt.items.reorder'}}</span>
                <span class="account-item-detail-value">{{{reorder_message}}}</span>
              </div>
            {{/unless}}
          </div>
        </div>

        <div class="account-item-badge">{{total.formatted}}</div>
      </article>
    {{/each}}
  </div>
{{/if}}

core/account/_private/receipt-shipments:
{{#if shipments}}
  <section class="account-shipments">
    <h2 class="account-sub-heading">{{lang 'core.account.receipt.shipments.title'}}</h2>

    <table class="table-clean account-shipments-table">
      <tr>
        <th>{{lang 'core.account.receipt.shipments.date'}}</th>
        <th>{{lang 'core.account.receipt.shipments.provider'}}</th>
        <th>{{lang 'core.account.receipt.shipments.link'}}</th>
      </tr>
      {{#each shipments}}
        <tr>
          <td>{{date_shipped}}</td>
          <td>{{shipping_provider}}</td>
          <td>
            {{#if shipping_track.url}}
              <a href="{{shipping_track.url}}">
            {{/if}}

            {{#if show_shipping_method}}
              {{shipping_track.number}} &mdash; {{shipping_method}}
            {{else}}
              {{shipping_track.number}}
            {{/if}}

            {{#if shipping_track.url}}
              </a>
            {{/if}}
          </td>
        </tr>
      {{/each}}
    </table>
  </section>
{{/if}}

core/account/orders/details:
{{!
  Markup and styles related to this page use `receipt` instead of
  `order-details`. Receipt is shorter and doesn't conflict with other "details"
  sections.
}}

{{#partial 'account-heading'}}
  {{lang 'core.account.receipt.heading' number=order.id}}
{{/partial}}

{{#partial 'account-toolbar'}}
  <a class="account-tool" href="{{order.invoice_url}}" target="_blank">{{lang 'core.account.receipt.actions.print_invoice'}}</a>

  <form class="account-order-invoice" action="{{order.reorder_url}}" method="post">
    <input type="hidden" name="orderid" value="{{order.id}}">
    {{#each order.items}}
      <input type="hidden" name="reorderitem[{{order_product_id}}]">
    {{/each}}
    <input class="account-tool" type="submit" value="{{lang 'core.account.receipt.actions.reorder_items'}}">
  </form>

  {{#if order.is_complete}}
    {{#if order.returns_enabled}}
      <a href="{{order.return_url}}" class="account-tool">{{lang 'core.account.receipt.actions.return_items'}}</a>
    {{/if}}
  {{/if}}
{{/partial}}

{{#partial 'account-content'}}
  {{! Summary }}
  {{> core/account/_private/receipt-summary}}

  {{! Comments }}
  {{#if order.comments}}
    <section class="account-receipt-comments account-card">{{order.comments}}</section>
  {{/if}}

  {{! Shipments }}
  {{> core/account/_private/receipt-shipments}}

  {{! Products }}
  {{> core/account/_private/receipt-items}}

  {{! Totals }}
  <div class="account-receipt-totals">
    {{#each order.total_rows}}
      {{#if value.value}}
        <div class="account-receipt-total">
          <div class="account-receipt-total-title">{{label}}</div>
          <div class="account-receipt-total-value">{{value.formatted}}</div>
        </div>
      {{/if}}
    {{/each}}
  </div>
{{/partial}}

